mapper.select:
	SELECT 
      id_cotizacion_adjunto_rechazada , 
      archivo_id , 
      archivo_nombre , 
      archivo_tipo , 
      descripcion , 
      ind_cotizacion , 
      ruta_adjunto 
	FROM
	cotizacion_adjunto_rechazada
	WHERE 1 = 1

		AND id_cotizacion_adjunto_rechazada = #{id, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND descripcion = #{descripcion}


		AND ind_cotizacion = #{indCotizacion}


		AND ruta_adjunto = #{rutaAdjunto}

mapper.insert:
INSERT INTO cotizacion_adjunto_rechazada( id_cotizacion_adjunto_rechazada , archivo_id , archivo_nombre , archivo_tipo , descripcion , ind_cotizacion , ruta_adjunto ) (
SELECT #{element.id} , #{element.archivoId} , #{element.archivoNombre} , #{element.archivoTipo} , #{element.descripcion} , #{element.indCotizacion} , #{element.rutaAdjunto} from dummy
)

mapper.delete:
    DELETE FROM cotizacion_adjunto_rechazada
	WHERE 1 = 1

		AND id_cotizacion_adjunto_rechazada = #{id, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND descripcion = #{descripcion}


		AND ind_cotizacion = #{indCotizacion}


		AND ruta_adjunto = #{rutaAdjunto}
