mapper.select:
	SELECT 
      id_cotizacion_campo_respuesta_rechazada , 
      comentario , 
      fecha_registro , 
      peso , 
      puntaje , 
      texto_respuesta_libre 
	FROM
	cotizacion_campo_respuesta_rechazada
	WHERE 1 = 1

		AND id_cotizacion_campo_respuesta_rechazada = #{id, jdbcType=INTEGER}


		AND comentario = #{comentario}


		AND fecha_registro = #{fechaRegistro, jdbcType=TIMESTAMP}


		AND peso = #{peso, jdbcType=NUMERIC}


		AND puntaje = #{puntaje, jdbcType=NUMERIC}


		AND texto_respuesta_libre = #{textoRespuestaLibre}

mapper.insert:
INSERT INTO cotizacion_campo_respuesta_rechazada( id_cotizacion_campo_respuesta_rechazada , comentario , fecha_registro , peso , puntaje , texto_respuesta_libre ) (
SELECT #{element.id} , #{element.comentario} , #{element.fechaRegistro} , #{element.peso} , #{element.puntaje} , #{element.textoRespuestaLibre} from dummy
)

mapper.delete:
    DELETE FROM cotizacion_campo_respuesta_rechazada
	WHERE 1 = 1

		AND id_cotizacion_campo_respuesta_rechazada = #{id, jdbcType=INTEGER}


		AND comentario = #{comentario}


		AND fecha_registro = #{fechaRegistro, jdbcType=TIMESTAMP}


		AND peso = #{peso, jdbcType=NUMERIC}


		AND puntaje = #{puntaje, jdbcType=NUMERIC}


		AND texto_respuesta_libre = #{textoRespuestaLibre}
