mapper.select:
	SELECT 
      id_cotizacion_detalle_rechazada , 
      cantidad_cotizada , 
      cantidad_solicitada , 
      descripcion , 
      especificacion , 
      ind_ganador , 
      numero_parte , 
      precio_unitario , 
      solicitud_pedido 
	FROM
	cotizacion_detalle_rechazada
	WHERE 1 = 1

		AND id_cotizacion_detalle_rechazada = #{id, jdbcType=INTEGER}


		AND cantidad_cotizada = #{cantidadCotizada, jdbcType=NUMERIC}


		AND cantidad_solicitada = #{cantidadSolicitada, jdbcType=NUMERIC}


		AND descripcion = #{descripcion}


		AND especificacion = #{especificacion}


		AND ind_ganador = #{indGanador}


		AND numero_parte = #{numeroParte}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND solicitud_pedido = #{solicitudPedido}

mapper.insert:
INSERT INTO cotizacion_detalle_rechazada( id_cotizacion_detalle_rechazada , cantidad_cotizada , cantidad_solicitada , descripcion , especificacion , ind_ganador , numero_parte , precio_unitario , solicitud_pedido ) (
SELECT #{element.id} , #{element.cantidadCotizada} , #{element.cantidadSolicitada} , #{element.descripcion} , #{element.especificacion} , #{element.indGanador} , #{element.numeroParte} , #{element.precioUnitario} , #{element.solicitudPedido} from dummy
)

mapper.delete:
    DELETE FROM cotizacion_detalle_rechazada
	WHERE 1 = 1

		AND id_cotizacion_detalle_rechazada = #{id, jdbcType=INTEGER}


		AND cantidad_cotizada = #{cantidadCotizada, jdbcType=NUMERIC}


		AND cantidad_solicitada = #{cantidadSolicitada, jdbcType=NUMERIC}


		AND descripcion = #{descripcion}


		AND especificacion = #{especificacion}


		AND ind_ganador = #{indGanador}


		AND numero_parte = #{numeroParte}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND solicitud_pedido = #{solicitudPedido}
