mapper.select:
	SELECT 
      id_indicador , 
      codigo_tipo_valor_indicador , 
      valor_minimo , 
      valor_maximo , 
      valor_intermedio , 
      descripcion_indicador 
	FROM
	indicador
	WHERE 1 = 1

		AND id_indicador = #{id, jdbcType=INTEGER}


		AND codigo_tipo_valor_indicador = #{codigoTipoValorIndicador}


		AND valor_minimo = #{valorMinimo, jdbcType=NUMERIC}


		AND valor_maximo = #{valorMaximo, jdbcType=NUMERIC}


		AND valor_intermedio = #{valorIntermedio, jdbcType=NUMERIC}


		AND descripcion_indicador = #{descripcionIndicador}

mapper.insert:
INSERT INTO indicador( id_indicador , codigo_tipo_valor_indicador , valor_minimo , valor_maximo , valor_intermedio , descripcion_indicador ) (
SELECT #{element.id} , #{element.codigoTipoValorIndicador} , #{element.valorMinimo} , #{element.valorMaximo} , #{element.valorIntermedio} , #{element.descripcionIndicador} from dummy
)

mapper.delete:
    DELETE FROM indicador
	WHERE 1 = 1

		AND id_indicador = #{id, jdbcType=INTEGER}


		AND codigo_tipo_valor_indicador = #{codigoTipoValorIndicador}


		AND valor_minimo = #{valorMinimo, jdbcType=NUMERIC}


		AND valor_maximo = #{valorMaximo, jdbcType=NUMERIC}


		AND valor_intermedio = #{valorIntermedio, jdbcType=NUMERIC}


		AND descripcion_indicador = #{descripcionIndicador}
