mapper.select:
	SELECT 
      id_licitacion_adjunto_respuesta , 
      id_licitacion , 
      archivo_id , 
      archivo_nombre , 
      archivo_tipo , 
      descripcion , 
      ruta_adjunto 
	FROM
	licitacion_adjunto_respuesta
	WHERE 1 = 1

		AND id_licitacion_adjunto_respuesta = #{id, jdbcType=INTEGER}


		AND id_licitacion = #{idLicitacion, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND descripcion = #{descripcion}


		AND ruta_adjunto = #{rutaAdjunto}

mapper.insert:
INSERT INTO licitacion_adjunto_respuesta( id_licitacion_adjunto_respuesta , id_licitacion , archivo_id , archivo_nombre , archivo_tipo , descripcion , ruta_adjunto ) (
SELECT #{element.id} , #{element.idLicitacion} , #{element.archivoId} , #{element.archivoNombre} , #{element.archivoTipo} , #{element.descripcion} , #{element.rutaAdjunto} from dummy
)

mapper.delete:
    DELETE FROM licitacion_adjunto_respuesta
	WHERE 1 = 1

		AND id_licitacion_adjunto_respuesta = #{id, jdbcType=INTEGER}


		AND id_licitacion = #{idLicitacion, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND descripcion = #{descripcion}


		AND ruta_adjunto = #{rutaAdjunto}
