mapper.select:
	SELECT 
      id_licitacion_proveedor_detalle_evaluacion , 
      factor_denominador_tasa , 
      tasa_moneda , 
      peso_moneda , 
      peso_condicion , 
      peso_evaluacion_tecnica , 
      precio_suma , 
      tasa_interes_moneda , 
      costo_financiero_moneda , 
      precio_final_moneda , 
      precio_final_minimo_moneda , 
      puntaje_condicion , 
      puntaje_maximo_condicion , 
      puntaje_evaluacion_tecnica , 
      porcentaje_precio_obtenido , 
      porcentaje_condicion_obtenido , 
      porcentaje_evaluacion_tecnica_obtenido , 
      porcentaje_total_obtenido , 
      fecha_tasa_cambio , 
      tasa_cambio , 
      cantidad_total_licitacion , 
      cantidad_total_cotizacion 
	FROM
	licitacion_proveedor_detalle_evaluacion
	WHERE 1 = 1

		AND id_licitacion_proveedor_detalle_evaluacion = #{id, jdbcType=INTEGER}


		AND factor_denominador_tasa = #{factorDenominadorTasa, jdbcType=INTEGER}


		AND tasa_moneda = #{tasaMoneda, jdbcType=NUMERIC}


		AND peso_moneda = #{pesoMoneda, jdbcType=NUMERIC}


		AND peso_condicion = #{pesoCondicion, jdbcType=NUMERIC}


		AND peso_evaluacion_tecnica = #{pesoEvaluacionTecnica, jdbcType=NUMERIC}


		AND precio_suma = #{precioSuma, jdbcType=NUMERIC}


		AND tasa_interes_moneda = #{tasaInteresMoneda, jdbcType=NUMERIC}


		AND costo_financiero_moneda = #{costoFinancieroMoneda, jdbcType=NUMERIC}


		AND precio_final_moneda = #{precioFinalMoneda, jdbcType=NUMERIC}


		AND precio_final_minimo_moneda = #{precioFinalMinimoMoneda, jdbcType=NUMERIC}


		AND puntaje_condicion = #{puntajeCondicion, jdbcType=NUMERIC}


		AND puntaje_maximo_condicion = #{puntajeMaximoCondicion, jdbcType=NUMERIC}


		AND puntaje_evaluacion_tecnica = #{puntajeEvaluacionTecnica, jdbcType=NUMERIC}


		AND porcentaje_precio_obtenido = #{porcentajePrecioObtenido, jdbcType=NUMERIC}


		AND porcentaje_condicion_obtenido = #{porcentajeCondicionObtenido, jdbcType=NUMERIC}


		AND porcentaje_evaluacion_tecnica_obtenido = #{porcentajeEvaluacionTecnicaObtenido, jdbcType=NUMERIC}


		AND porcentaje_total_obtenido = #{porcentajeTotalObtenido, jdbcType=NUMERIC}


		AND fecha_tasa_cambio = #{fechaTasaCambio, jdbcType=TIMESTAMP}


		AND tasa_cambio = #{tasaCambio, jdbcType=NUMERIC}


		AND cantidad_total_licitacion = #{cantidadTotalLicitacion, jdbcType=NUMERIC}


		AND cantidad_total_cotizacion = #{cantidadTotalCotizacion, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO licitacion_proveedor_detalle_evaluacion( id_licitacion_proveedor_detalle_evaluacion , factor_denominador_tasa , tasa_moneda , peso_moneda , peso_condicion , peso_evaluacion_tecnica , precio_suma , tasa_interes_moneda , costo_financiero_moneda , precio_final_moneda , precio_final_minimo_moneda , puntaje_condicion , puntaje_maximo_condicion , puntaje_evaluacion_tecnica , porcentaje_precio_obtenido , porcentaje_condicion_obtenido , porcentaje_evaluacion_tecnica_obtenido , porcentaje_total_obtenido , fecha_tasa_cambio , tasa_cambio , cantidad_total_licitacion , cantidad_total_cotizacion ) (
SELECT #{element.id} , #{element.factorDenominadorTasa} , #{element.tasaMoneda} , #{element.pesoMoneda} , #{element.pesoCondicion} , #{element.pesoEvaluacionTecnica} , #{element.precioSuma} , #{element.tasaInteresMoneda} , #{element.costoFinancieroMoneda} , #{element.precioFinalMoneda} , #{element.precioFinalMinimoMoneda} , #{element.puntajeCondicion} , #{element.puntajeMaximoCondicion} , #{element.puntajeEvaluacionTecnica} , #{element.porcentajePrecioObtenido} , #{element.porcentajeCondicionObtenido} , #{element.porcentajeEvaluacionTecnicaObtenido} , #{element.porcentajeTotalObtenido} , #{element.fechaTasaCambio} , #{element.tasaCambio} , #{element.cantidadTotalLicitacion} , #{element.cantidadTotalCotizacion} from dummy
)

