mapper.select:
	SELECT 
      id_licitacion_proveedor_pregunta , 
      id_licitacion , 
      id_proveedor , 
      pregunta 
	FROM
	licitacion_proveedor_pregunta
	WHERE 1 = 1

		AND id_licitacion_proveedor_pregunta = #{id, jdbcType=INTEGER}


		AND id_licitacion = #{idLicitacion, jdbcType=INTEGER}


		AND id_proveedor = #{idProveedor, jdbcType=INTEGER}


		AND pregunta = #{pregunta}

mapper.insert:
INSERT INTO licitacion_proveedor_pregunta( id_licitacion_proveedor_pregunta , id_licitacion , id_proveedor , pregunta ) (
SELECT #{element.id} , #{element.idLicitacion} , #{element.idProveedor} , #{element.pregunta} from dummy
)

mapper.delete:
    DELETE FROM licitacion_proveedor_pregunta
	WHERE 1 = 1

		AND id_licitacion_proveedor_pregunta = #{id, jdbcType=INTEGER}


		AND id_licitacion = #{idLicitacion, jdbcType=INTEGER}


		AND id_proveedor = #{idProveedor, jdbcType=INTEGER}


		AND pregunta = #{pregunta}
