mapper.select:
	SELECT 
      id_licitacion_proveedor_renegociacion , 
      fecha_cierre_recepcion , 
      motivo_renegociacion 
	FROM
	licitacion_proveedor_renegociacion
	WHERE 1 = 1

		AND id_licitacion_proveedor_renegociacion = #{id, jdbcType=INTEGER}


		AND fecha_cierre_recepcion = #{fechaCierreRecepcion, jdbcType=TIMESTAMP}


		AND motivo_renegociacion = #{motivoRenegociacion}

mapper.insert:
INSERT INTO licitacion_proveedor_renegociacion( id_licitacion_proveedor_renegociacion , fecha_cierre_recepcion , motivo_renegociacion ) (
SELECT #{element.id} , #{element.fechaCierreRecepcion} , #{element.motivoRenegociacion} from dummy
)

mapper.delete:
    DELETE FROM licitacion_proveedor_renegociacion
	WHERE 1 = 1

		AND id_licitacion_proveedor_renegociacion = #{id, jdbcType=INTEGER}


		AND fecha_cierre_recepcion = #{fechaCierreRecepcion, jdbcType=TIMESTAMP}


		AND motivo_renegociacion = #{motivoRenegociacion}
