mapper.select:
	SELECT 
      id_licitacion_subestapa , 
      fecha_cierre_subetapa , 
      fecha_envio_correo_recordatorio 
	FROM
	licitacion_subetapa
	WHERE 1 = 1

		AND id_licitacion_subestapa = #{id, jdbcType=INTEGER}


		AND fecha_cierre_subetapa = #{fechaCierreSubetapa, jdbcType=TIMESTAMP}


		AND fecha_envio_correo_recordatorio = #{fechaEnvioCorreoRecordatorio, jdbcType=TIMESTAMP}

mapper.insert:
INSERT INTO licitacion_subetapa( id_licitacion_subestapa , fecha_cierre_subetapa , fecha_envio_correo_recordatorio ) (
SELECT #{element.id} , #{element.fechaCierreSubetapa} , #{element.fechaEnvioCorreoRecordatorio} from dummy
)

mapper.delete:
    DELETE FROM licitacion_subetapa
	WHERE 1 = 1

		AND id_licitacion_subestapa = #{id, jdbcType=INTEGER}


		AND fecha_cierre_subetapa = #{fechaCierreSubetapa, jdbcType=TIMESTAMP}


		AND fecha_envio_correo_recordatorio = #{fechaEnvioCorreoRecordatorio, jdbcType=TIMESTAMP}
