mapper.select:
	SELECT 
      id_tasa_cambio , 
      fecha_tasa , 
      valor 
	FROM
	tasa_cambio
	WHERE 1 = 1

		AND id_tasa_cambio = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=TIMESTAMP}


		AND valor = #{valor, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO tasa_cambio( id_tasa_cambio , fecha_tasa , valor ) (
SELECT #{element.id} , #{element.fechaTasa} , #{element.valor} from dummy
)

mapper.delete:
    DELETE FROM tasa_cambio
	WHERE 1 = 1

		AND id_tasa_cambio = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=TIMESTAMP}


		AND valor = #{valor, jdbcType=NUMERIC}
