mapper.select:
	SELECT 
      id_bien_servicio , 
      codigo_sap , 
      descripcion , 
      numero_parte , 
      tipo_item , 
      codigo_rubro_sap , 
      codigo_unidad_medida_sap , 
      descripcion_larga 
	FROM
	temp_bien_servicio
	WHERE 1 = 1

		AND id_bien_servicio = #{id, jdbcType=INTEGER}


		AND codigo_sap = #{codigoSap}


		AND descripcion = #{descripcion}


		AND numero_parte = #{numeroParte}


		AND tipo_item = #{tipoItem}


		AND codigo_rubro_sap = #{codigoRubroSap, jdbcType=INTEGER}


		AND codigo_unidad_medida_sap = #{codigoUnidadMedidaSap, jdbcType=INTEGER}


		AND descripcion_larga = #{descripcionLarga}

mapper.insert:
INSERT INTO temp_bien_servicio( id_bien_servicio , codigo_sap , descripcion , numero_parte , tipo_item , codigo_rubro_sap , codigo_unidad_medida_sap , descripcion_larga ) (
SELECT #{element.id} , #{element.codigoSap} , #{element.descripcion} , #{element.numeroParte} , #{element.tipoItem} , #{element.codigoRubroSap} , #{element.codigoUnidadMedidaSap} , #{element.descripcionLarga} from dummy
)

mapper.delete:
    DELETE FROM temp_bien_servicio
	WHERE 1 = 1

		AND id_bien_servicio = #{id, jdbcType=INTEGER}


		AND codigo_sap = #{codigoSap}


		AND descripcion = #{descripcion}


		AND numero_parte = #{numeroParte}


		AND tipo_item = #{tipoItem}


		AND codigo_rubro_sap = #{codigoRubroSap, jdbcType=INTEGER}


		AND codigo_unidad_medida_sap = #{codigoUnidadMedidaSap, jdbcType=INTEGER}


		AND descripcion_larga = #{descripcionLarga}
