mapper.select:
	SELECT 
      id_rubro , 
      codigo_sap , 
      descripcion , 
      nivel 
	FROM
	temp_rubro_bien
	WHERE 1 = 1

		AND id_rubro = #{id, jdbcType=INTEGER}


		AND codigo_sap = #{codigoSap}


		AND descripcion = #{descripcion}


		AND nivel = #{nivel, jdbcType=INTEGER}

